<?phpfunction ti_constructzine_theme_setup(){	global $content_width;	if ( ! isset( $content_width ) ) {		$content_width = 634;	}	require_once( 'includes/custom-functions.php' );	require_once( 'includes/customizer.php' );	add_theme_support( 'post-thumbnails' );	add_theme_support( 'automatic-feed-links' );	$args = array(		'default-image'          => get_template_directory_uri() . '/images/header.png',		'random-default'         => false,		'width'                  => 1903,		'height'                 => 720,		'flex-height'            => false,		'flex-width'             => false,		'default-text-color'     => '',		'header-text'            => false,		'uploads'                => true,		'wp-head-callback'       => '',		'admin-head-callback'    => '',		'admin-preview-callback' => '',	);	add_theme_support( 'custom-header', $args );}/**	Constructzine Style*/function ti_constructzine_frontend_files() {	wp_enqueue_style( 'style', get_stylesheet_uri(), array(), '1.0', false );	if ( is_singular() ) {		wp_enqueue_script( "comment-reply" );	}	wp_enqueue_script( 'jquery' );	wp_enqueue_script( 'carouFredSel', get_template_directory_uri() . '/js/jquery.carouFredSel-6.2.1-packed.js', array( 'jquery' ), '6.2.1', true );	wp_enqueue_script( 'custom', get_template_directory_uri() . '/js/custom.js', array( 'jquery' ), '1.0', true );}add_action( 'wp_enqueue_scripts', 'ti_constructzine_frontend_files' );function ti_constructzine_menu() {	$locations = array(		'top-menu' => __( 'Top Header', 'ti' ),	);	register_nav_menus( $locations );}add_action( 'init', 'ti_constructzine_menu' );add_action( 'after_setup_theme', 'ti_constructzine_theme_setup' );function ti_constructzine_sidebar() {	$args = array(		'id'            => 'general_sidebar',		'name'          => __( 'General Sidebar', 'ti' ),		'description'   => __( 'This sidebar will appear in blog page.', 'ti' ),		'before_title'  => '<div class="widget-title">',		'after_title'   => '</div>',		'before_widget' => '<div id="%1$s" class="widget %2$s">',		'after_widget'  => '</div>',	);	register_sidebar( $args );	$args = array(		'id'            => 'home_sidebar',		'name'          => __( 'Homepage Sidebar', 'ti' ),		'description'   => __( 'This sidebar will appear on the frontpage', 'ti' ),		'before_title'  => '<h2>',		'after_title'   => '</h2>',		'before_widget' => '<div  id="%1$s" class="feedback widget block %2$s">',		'after_widget'  => '</div>',	);	register_sidebar( $args );}add_action( 'widgets_init', 'ti_constructzine_sidebar' );